import React, { useState } from "react";

const Counter = () => {
  // let counter= 0;
  const [counter, setCounter] = useState(12);
  const increase = () => {
    counter += 1;
   setCounter(counter+1);
  };
  return (
    <>
      <h1>{counter}</h1>
      <button>-</button>
      <button onClick={increase}>+</button>
    </>
  );
};

export default Counter;
